import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import "../css/BlogDescription.css"; // Add styles for this page

function BlogDescription() {
  const { id } = useParams(); // Get blog ID from the URL
  console.log("Fetching blog with ID:", id);

  const [blog, setBlog] = useState(null);
  const [submit,issubmitted] =useState({
    name:"",
    email:""
  });

  const handleChange =(e)=>{
    const{name,value}=e.target;
    issubmitted({...submit,[name]: value});

  }
console.log(submit);

  useEffect(() => {
    axios
      .get(`http://localhost:5001/api/LatestBlogs/${id}`) // Ensure correct API endpoint
      .then((response) => {
        setBlog(response.data);
      })
      .catch((error) => console.error("Error fetching blog:", error));
  }, [id]);

  if (!blog) {
    return <p className="loading">Loading...</p>;
  }

  return (
    <div className="Main-page">
    <div className="blog-content-container">
      <h1 className="blog-Heading">{blog.name}</h1>
      <img
        src={`http://localhost:5001/uploads/${blog.descriptionImg}`}
        alt={blog.name}
        className="blog-Img"
      />
      <p className="blog-date">Published on {blog.date}</p>
      <div className="blog-Info">{blog.Content}</div>
    </div>


    <div className="Form-container" >
      <div className="forms">
        <div className=" input-section">
          <form className="form">
            <label> Name:</label>
            <input 
            type="text"
            id="name"
            name="name"
            value={submit.name}
            onChange={handleChange}
            placeholder="Name"
            className="input">
            </input>

            <label>Email:</label>
            <input 
            type="email"
            id="email"
            name="email"
            value={submit.email}
            onChange={handleChange}
            placeholder="Email"
            className="input">
            </input>

            <input
            id="Review"
            name="Review"
            value={submit.vau}
            >Enter your Review </input>
          </form>
        </div>

        <button >submit </button>

      </div>
    </div>
    </div>
  );
}
export default BlogDescription;
