import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import "../css/BlogDescription.css"; // Add styles for this page

function BlogDescription() {
  const { id } = useParams();
  console.log("Fetching blog with ID:", id);

  const [blog, setBlog] = useState(null);
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [review, setReview] = useState("");
  const [reviews, setReviews] = useState([]); 

  useEffect(() => {
    const storedReviews = localStorage.getItem("reviews");
    console.log("Stored Reviews:", storedReviews); 
    if (storedReviews) {
      setReviews(JSON.parse(storedReviews));
    }
  }, []);


  const handleSubmit = (e) => {
    e.preventDefault();

    if (!name || !email || !review) {
      alert("All fields are required!");
      return;
    }

    const newReview = { name, email, review };
    const updatedReviews = [...reviews, newReview];

    setReviews(updatedReviews);
    localStorage.setItem("reviews", JSON.stringify(updatedReviews));

    console.log("Saved in LocalStorage:", JSON.stringify(updatedReviews)); // Debugging line

    setName("");
    setEmail("");
    setReview("");
  };


  useEffect(() => {
    axios
      .get(`http://localhost:5001/api/LatestBlogs/${id}`) 
      .then((response) => {
        setBlog(response.data);
      })
      .catch((error) => console.error("Error fetching blog:", error));
  }, [id]);

  if (!blog) {
    return <p className="loading">Loading...</p>;
  }



  return (
    <div className="Main-page">
    <div className="blog-content-container">
      <h1 className="blog-Heading">{blog.name}</h1>
      <img
        src={`http://localhost:5001/uploads/${blog.DescriptionImage}`}
        alt={blog.name}
        className="blog-Img"
      />
      <p className="blog-date">Published on {blog.date}</p>
      <div className="blog-Info">{blog.content}</div>
    </div>


    <h2 className="text-xl font-bold mb-4">Submit a Review</h2>

<form onSubmit={handleSubmit} className="space-y-3">
  <input
    type="text"
    placeholder="Name"
    value={name}
    onChange={(e) => setName(e.target.value)}
    className="w-full p-2 border rounded"
  />
  <input
    type="email"
    placeholder="Email"
    value={email}
    onChange={(e) => setEmail(e.target.value)}
    className="w-full p-2 border rounded"
  />
  <textarea
    placeholder="Write your review..."
    value={review}
    onChange={(e) => setReview(e.target.value)}
    className="w-full p-2 border rounded"
  ></textarea>
  <button type="submit" className="bg-blue-500 text-white p-2 rounded">
    Submit Review
  </button>
</form>

{/* Display submitted reviews */}
<div className="mt-6">
  <h3 className="text-lg font-bold">User Reviews</h3>
  {reviews.length === 0 ? (
    <p className="text-gray-500">No reviews yet.</p>
  ) : (
    <div className="space-y-3 mt-3">
      {reviews.map((r, index) => (
        <div key={index} className="border p-3 rounded">
          <h4 className="font-semibold">{r.name}</h4>
          <p className="text-sm text-gray-500">{r.email}</p>
          <p className="mt-1">{r.review}</p>
        </div>
      ))}
    </div>
  )}
</div>
</div>


  );
}
export default BlogDescription;
