// 
import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import "../css/LatestBlogs.css";

function Dsa() {
  const [blogs, setBlogs] = useState([]);
  const [reviews, setReviews] = useState(
    JSON.parse(localStorage.getItem("reviews")) || []
  );
  const [reviewText, setReviewText] = useState("");

  // Fetch blog data
  useEffect(() => {
    axios
      .get("http://localhost:5001/api/DsaBlogs")
      .then((response) => {
        setBlogs(response.data);
      })
      .catch((error) => console.error("Error fetching Latest Blogs:", error));
  }, []);

  // Handle review submission
  const handleReviewSubmit = (e) => {
    e.preventDefault();
    const newReview = {
      text: reviewText,
      date: new Date().toLocaleString(),
    };

    // Update state and localStorage
    const updatedReviews = [...reviews, newReview];
    setReviews(updatedReviews);
    localStorage.setItem("reviews", JSON.stringify(updatedReviews));
    setReviewText("");
  };

  return (
    <>
      <div className="Latest-container">
        <h1 className="Latest-title">Data Structure & Algorithms</h1>
        <div className="blog-list">
          {blogs.map((blog, index) => (
            <div key={index} className="blog-card">
              <div className="blog-image">
                <Link to={`/blog-description/${blog._id}`} key={index} className="blog-card-link">
                  <img
                    src={`http://localhost:5001/uploads/${blog.image}`}
                    alt={blog.name}
                  />
                </Link>
              </div>
              <div className="blog-content">
                <Link to={`/blogs-description/${blog._id}`} key={index} className="blog-card-link">
                  <h2 className="blog-title-latest">{blog.name}</h2>
                </Link>
                <p className="blog-description">{blog.description}</p>
                <Link to={`/blog-description/${blog._id}`} key={index} className="blog-card-link">
                  <button className="read-more-btn">Read More</button>
                </Link>
                <p className="blog-date">Published on {blog.date}</p>
              </div>
            </div>
          ))}
        </div>

        {/* Reviews Section */}
        <div className="review-section">
          <h2>Leave a Review</h2>
          <form onSubmit={handleReviewSubmit} className="review-form">
            <textarea
              value={reviewText}
              onChange={(e) => setReviewText(e.target.value)}
              placeholder="Write your review..."
              className="review-input"
            />
            <button type="submit" className="submit-review-btn">Submit Review</button>
          </form>

          {/* Display Reviews */}
          <div className="reviews-list">
            {reviews.length > 0 ? (
              reviews.map((review, index) => (
                <div key={index} className="review-card">
                  <p className="review-text">{review.text}</p>
                  <span className="review-date">{review.date}</span>
                </div>
              ))
            ) : (
              <p>No reviews yet. Be the first to leave a review!</p>
            )}
          </div>
        </div>
      </div>
    </>
  );
}

export default Dsa;
