import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import "../css/BlogDescription.css"; // Add styles for this page

function BlogDescription() {
  const { id } = useParams(); // Get blog ID from the URL
  const [blog, setBlog] = useState(null);
  const [error, setError] = useState(null); // State for error handling

  useEffect(() => {
    axios
      .get(`http://localhost:5001/api/DsaBlogs/${id}`) // Fetch blog by ID
      .then((response) => {
        setBlog(response.data);
        setError(null); // Reset error on successful response
      })
      .catch((error) => {
        console.error("Error fetching blog:", error);
        setError("Error fetching blog details. Please try again later.");
      });
  }, [id]);

  if (error) {
    return <p className="error-message">{error}</p>; // Display error message if there's an error
  }

  if (!blog) {
    return <p className="loading">Loading...</p>; // Show loading state if blog is not yet fetched
  }

  // Format date (assuming it is in ISO format)
  const formattedDate = new Date(blog.date).toLocaleDateString();

  return (
    <div className="blog-description-container">
      <h1 className="blog-title">{blog.name}</h1>
      <img
        src={`http://localhost:5001/uploads/${blog.image}`} // Corrected the field name here
        alt={blog.name}
        className="blog-image"
      />
      <p className="blog-date">Published on {formattedDate}</p>
      <p className="blog-content">{blog.description}</p>
    </div>
  );
}

export default BlogDescription;
