import React, { useEffect, useState } from 'react';
import axios from 'axios'; // Make sure axios is installed
import '../css/Dsa.css';

function Dsa() {
  const [blogs, setBlogs] = useState([]); // State to store blog data

  useEffect(() => {
    // Fetch data from the backend
    axios
      .get('http://localhost:5001/api/DsaBlogs') // Replace with the correct API endpoint
      .then((response) => {
        setBlogs(response.data); // Store fetched blogs in state
      })
      .catch((error) => console.error('Error fetching Latest Blogs:', error));
  }, []);

  return (
    <>
      <div className="Latest-container">
        <h1 className="Latest-title">Latest Blogs</h1>
        <div className="blog-list">
          {/* Dynamically render blogs */}
          {blogs.map((blog, index) => (
            <div key={index} className="blog-card">
              <div className="blog-image">
                {/* Display blog image */}
                <img src={`http://localhost:5001/${blog.image}`} alt={blog.name} />
              </div>
              <div className="blog-content">
                {/* Blog title */}
                <h2 className="blog-title-latest">{blog.name}</h2>
                {/* Blog description */}
                <p className="blog-description">{blog.description}</p>
                {/* Read more link (Update link as per your routing) */}
                <a href={`#blog-description`} className="read-more-btn">
                  Read More
                </a>
                {/* Blog date */}
                <p className="blog-date">Published on {blog.date}</p>
              </div>
            </div>
          ))}
        </div>
      </div>

    </>
  );
}

export default Dsa;
