import { Link } from 'react-router-dom';
import { useEffect } from 'react';
import '../css/Home.css';

const Home = () => {
  useEffect(() => {
    const observer = new IntersectionObserver((entries) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          entry.target.classList.add('show');
        }
      });
    }, { threshold: 0.1 });

    document.querySelectorAll('.animate-on-scroll').forEach((el) => observer.observe(el));
  }, []);

  return (
    <div className="home-container">
      {/* Background Elements */}
      <div className="geometric-layer"></div>
      <div className="floating-shapes"></div>
      <div className="particle-overlay"></div>

      {/* Hero Section */}
      <section className="hero-section">
        <div className="title-container animate-on-scroll">
          <h1 className="main-title">MANY</h1>
          <h2 className="sub-title">DSA MASTERY.</h2>
        </div>

        <p className="hero-tagline animate-on-scroll">Reimagine the way you learn algorithms</p>

        <p className="hero-description animate-on-scroll">
          Curated learning paths delivered through interactive content, designed to help you
          conquer coding interviews and master complex concepts.
        </p>

        <div className="cta-container animate-on-scroll">
          <Link to="/learning-path" className="cta-button">
            BEGIN YOUR JOURNEY
          </Link>
        </div>
      </section>

      {/* New Feature Section */}
      <section className="features-section">
        <div className="feature-card animate-on-scroll">
          <h3>Interactive Code Editor</h3>
          <p>Practice algorithms directly in your browser with instant feedback</p>
        </div>
        <div className="feature-card animate-on-scroll">
          <h3>Progress Tracking</h3>
          <p>Visualize your learning journey with detailed analytics</p>
        </div>
        <div className="feature-card animate-on-scroll">
          <h3>Community Solutions</h3>
          <p>Compare your approaches with other developers' implementations</p>
        </div>
      </section>
    </div>
  );
};

export default Home;
