import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import "../css/BlogDescription.css"; // Add styles for this page

function BlogDescription() {
  const { id } = useParams(); // Get blog ID from the URL
  console.log("Fetching blog with ID:", id);

  const [blog, setBlog] = useState(null);

  useEffect(() => {
    axios
      .get(`http://localhost:5001/api/DsaBlogs/${id}`) // Ensure correct API endpoint
      .then((response) => {
        setBlog(response.data);
      })
      .catch((error) => console.error("Error fetching blog:", error));
  }, [id]);

  if (!blog) {
    return <p className="loading">Loading...</p>;
  }

  const formatContent = (content) => {
    if (content) {
      return content.split("\n").map((str, index) => (
        <span key={index}>
          {str}
          <br />
        </span>
      ));
    }
    return null;
  };

  return (
    <div className="blog-description-container">
      <h1 className="blog-title">{blog.name}</h1>
      <img
        src={`http://localhost:5001/uploads/${blog.descriptionImg}`}
        alt={blog.name}
        className="blog-image"
      />
      <p className="blog-date">Published on {blog.date}</p>
      <p className="blog-content">{blog.Content}</p>
    </div>
  );
}

export default BlogDescription;
